library(jagsUI)
library(tidyverse)
library(ggmcmc)

setwd("/Users/kayla/Documents/space-time")
ta <- read.csv("wholedataset_totalabundance.csv")
ta_obs <- read.csv("observerdataset_totalabundance.csv")

obsID <- select(ta_obs, c(ObsN, Obs_ID))
obsID <- obsID %>% distinct(ObsN, Obs_ID)
ta <- merge(ta, obsID, by = "ObsN", all.x = TRUE)

### set up main analysis taa
space.time <- ta$space.time # categorical
forest <- ta$Forest.cover # continuous 
region <- ta$Region
abund <- ta$TotalAbundance # count
observer <- ta$Obs_ID # categorical

### set up observer model data
route <- ta_obs$Route_ID # categorical - index variable
obs <- ta_obs$Obs_ID # categorical - index variable
abund_obs <- ta_obs$TotalAbundance
ecozone <- ta_obs$Eco_ID # categorical - index variable


### convert to percentage
p_forest <- 0.01*forest

### set up n's
nabund <- nrow(ta)
nobservers <- length(unique(observer)) # number of observers
nregions <- length(unique(region))
nabund_obs <- nrow(ta_obs) # number of observer counts
nobs <- length(unique(obs)) # number of observers
nroutes_obs <- length(unique(route)) # number of routes
necozones_obs <- length(unique(ecozone)) # number of ecozones


# use function so that inits apply to both chains (or all chains)
inits <- function() {
  list(sd_noiset = 1,
       sd_beta_modt = 1)
}


###############################
#    MODEL CODE!!!            #
###############################
modl <- "
model {

######### priors & constraints ###########
sd_noiset ~ dt(0, 1, 20) T(0,) # absolute value (truncated >0 ) of student's-t centred on 0 / half-t prior on standard deviation
sd_noise <- 0.1*sd_noiset # puts 95% of sdnoise below ~0.5 on the log scale
taunoise <- pow(sd_noise, -2) # converts back to precision (inverse of variance)

sd_route ~ dt(0, 1, 4) T(0,) # 95% below ~0.1 on the log scale
tau_route <- pow(sd_route, -2)
  
sd_obs ~ dt(0, 1, 4) T(0,)
tau_obs <- pow(sd_obs, -2)
      
sd_noise_obs ~ dt(0, 1, 4) T(0,)
tau_noise_obs <- pow(sd_noise_obs, -2)
    
sd_beta_modt ~ dt(0, 1, 4) T(0,)
sd_beta_mod <- 0.5 * sd_beta_modt
tau_beta_mod <- pow(sd_beta_mod, -2)

#sd_betat ~ dt(0, 1, 20) T(0,)
#sd_beta <- 0.5 * sd_betat
#tau_beta <- pow(sd_beta, -2)

  
######### observer model ###########
for(i in 1:nabund_obs) {
  log(lambda_obs[i]) <- obs_offset[obs[i]] + route_effect[route[i]] + ecozone_effect[ecozone[i]] + noise_obs[i]
  
  noise_obs[i] ~ dnorm(0, tau_noise_obs)
  
  abund_obs[i] ~ dpois(lambda_obs[i])
  }
  
  for(o in 1:nobs) {
    obs_offset[o] ~ dnorm(0,tau_obs)
  }
  
  for(r in 1:nroutes_obs) {
    route_effect[r] ~ dnorm(0, tau_route)
  }
  
  for(e in 1:necozones_obs) {
    ecozone_effect[e] ~ dnorm(0, 0.01)
  }
  

######### MAIN model ###########
for(k in 1:nabund) {
  log(lambda[k]) <- alpha[region[k]] + (beta_space_time[region[k],space.time[k]] * (p_forest[k] - 0.5)) + obs_offset[observer[k]] + noise[k]
  abund[k] ~ dpois(lambda[k])
  
  # priors
  noise[k] ~ dnorm(0, taunoise)
  
}

for(r in 1:nregions){

## prior on alpha
alpha[r] ~ dnorm(0, 0.01)

## priors on beta
beta_mod[r] ~ dnorm(0, tau_beta_mod)
beta_space_time[r,1] ~ dnorm(0,0.01) # or beta_space_time[s,1] ~ dnorm(0,tau.beta_space_time) if random effect
beta_space_time[r,2] <- beta_space_time[s,1] + beta_mod[s] # space slope == 2


}
}
"
cat(modl,file = "total_abundance.r")



library(rlist)
jags_dat <- list('abund' = abund,
                 'space.time' = space.time,
                 'p_forest' = p_forest,
                 'observer' = observer,
                 'nabund' = nabund,
                 'nregions' = nregions,
                 'nobservers' = nobservers,
                 # observer
                 'abund_obs' = abund_obs,
                 'obs' = obs,
                 'route' = route,
                 'ecozone' = ecozone,
                 'nobs' = nobs,
                 'nabund_obs' = nabund_obs,
                 'necozones_obs' = necozones_obs,
                 'nroutes_obs' = nroutes_obs)


# skipping wind for now b/c it's so big
parms <- c("beta_space_time",
           "sd_noise",
           "alpha",
           "beta_mod",
           "sd_beta_mod",
           "noise",
           "sd_noise_obs",
           "obs_offset")

# re-set R memory limit to be really big -------------

memory.limit(56000)

# get posterior samples ----------------------------

x = jagsUI(data = jags_dat,
           parameters.to.save = parms,
           n.chains = 2,
           n.adapt = 2000,
           n.burnin = 20000, # discard half the iterations re: gelman 
           n.thin = 50, # keep more
           inits = inits,
           n.iter = 40000,
           parallel = T,
           modules = NULL,
           model.file = "total_abundance.r")

list.save(x,"total_abundance_fixedeffects.RData")


summary(x)
print(x)
x$mean$beta_space_time #posterior means of the slope parameters
x$mean$beta_mod
x$n.eff

# have to do them separately b/c not enough memory
# set out = x to ease re-loading the .rdata file across different sessions
out_ggs_beta_space_time = ggs(x$samples,  family = "beta_space_time")
out_ggs_beta_mod = ggs(x$samples,  family = "beta_mod")
#out_ggs_beta_mod = ggs(x$samples,  family = "beta_diff")
out_ggs_alpha = ggs(x$samples,  family = "alpha")

out_ggs_sd_beta_mod = ggs(x$samples, family = "sd_beta_mod")
out_ggs_sd_noise = ggs(x$samples, family = "sd_noise")
out_ggs_obs_offset = ggs(x$samples, family = "obs_offset")
out_ggs_sd_noise_obs = ggs(x$samples, family = "sd_noise_obs")

ggmcmc(out_ggs_beta_space_time,file = "beta_space_time_summary_NOV30.pdf", family = "beta_space_time", param_page = 8)
ggmcmc(out_ggs_beta_mod,file = "beta_mod_summary_NOV30.pdf", family = "beta_mod", param_page = 8)
